import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class NewsContainer extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1
    }
  }

  async componentDidMount() {
    let url = "https://newsapi.org/v2/everything?q=tesla&from=2022-09-26&sortBy=publishedAt&apiKey=6ce455988b87444d88177911390ea868&page=1pageSize=20";
    let data = await fetch(url);
    let parsedData = await data.json()
    this.setState({ articles: parsedData.articles })
  }



  render() {
    return (
      <div className='container my-3'>
        <h2>NewsMonkey - Top Headlines</h2>
        <div className='row'>
          {this.state.articles.map((element) => {
            return (<div className='col-md-4' key={element.url}>
              <NewsItem title={element ? element.title.slice(0, 45) : ""} description={element ? element.description.slice(0, 88) : ""} imageUrl={element.urlToImage} newUrl={element.url} />
            </div>)
          })}
        </div>
        <div className="container d-flex justify-content-between">
          <button disabled={this.state.page<1} type="button" class="btn btn-dark" onClick={handlePrevClick}> &larr; Previous</button>
          <button type="button" class="btn btn-dark" onClick={handleNextClick}>Next &rarr;</button>
        </div>
      </div>
    )
  }
}

export default NewsContainer
